/*!
 * Module dependencies.
 */

var util = require('util')
  , hotplate = require('hotplate')
  , path = require('path')
;

var hooks = exports.hotHooks = {}


hooks.pageElements = function( done ){
  done( null,  { moduleName: 'hotCoreHandyCss', result: {
    csses: [ 'handyCss.css' ]
  } } );
}


hooks.clientPaths = function( done ){
  done( null, { moduleName:'hotCoreHandyCss', result: [ path.join(__dirname, '../client') ] } );
}


