var hotplate=require('hotplate');

exports.init = function(){

  console.log("Init started hereeeeeeeeeeeeeeeeeee");

  hotplate.addCss('testing', 'one.css');
  hotplate.addCss('testing', 'two.css');

  hotplate.addJs('testing', 'one.js');
  hotplate.addJs('testing', 'two.js');

  hotplate.app.use('/testing', testing);
}


function testing( req, res, next ){
  res.send( hotplate.renderPage('Title', "CONTENTS ARE HERE") );

} 
