var hotplate=require('hotplate');

exports.init = function(){

  console.log("Init started hereeeeeeeeeeeeeeeeeee");

  hotplate.csses.add('testing', 'one.css');
  hotplate.csses.add('testing', 'two.css');

  hotplate.jses.add('testing', 'one.js');
  hotplate.jses.add('testing', 'two.js');

  hotplate.vars.add('testing', 'var1' , {name:'tony', surname:'mobily' } );

  hotplate.app.use('/testing', testing);

}


function testing( req, res, next ){

  
  var csses = new hotplate.Csses();
  csses.add('testing','AH.css');

  res.send( hotplate.processPageTemplate( { 
    title: 'The title', 
    body : 'CONTENTS ARE HERE',
    csses: csses } ) );

} 
