define([
   "dojo/_base/declare",

   "dijit/_WidgetBase",
   "dijit/_TemplatedMixin",
   "dijit/_WidgetsInTemplateMixin",

   "dijit/layout/BorderContainer",
   "dijit/layout/StackContainer",
   "dijit/layout/TabContainer",
   "dijit/layout/ContentPane",

   "hotplate/hotDojoAuth/LogoutButton",
   "hotplate/hotDojoHooks/_TemplatedHooksMixin",

   "hotplate/bd/Dashboard",

   // "app/widgets/SearchPage",

   ], function(
     declare
     , _WidgetBase
     , _TemplatedMixin
     , _WidgetsInTemplateMixin

     , BorderContainer
     , StackContainer
     , TabContainer
     , ContentPane

     , LogoutButton
     , _TemplatedHooksMixin

     , Dashboard
     // , SearchPage

 ){
    // Create the "login" pane, based on a normal ContentPane
    return declare( [_WidgetBase, _TemplatedMixin, _WidgetsInTemplateMixin, _TemplatedHooksMixin ], {

      workspaceName: vars.hotDojoAppContainer.workspaceName,

      widgetsInTemplate: true,

      templateString: '' +
        '<div>' +
        '  <div class="bookingDojo" data-dojo-type="dijit.layout.BorderContainer" data-dojo-props="design: \'headline\'">' +
        '    <div data-dojo-type="dijit.layout.ContentPane" data-dojo-props="region: \'top\'">' +
        '       Booking Dojo <span data-dojo-type="hotplate.hotDojoAuth.LogoutButton" data-dojo-props="workspaceName: \'${workspaceName}\'"> </span>' +
        '   </div>' +
        '    <div data-dojo-type="dijit.layout.TabContainer" data-dojo-props="region: \'center\', tabPosition: \'left-h\'">' +
        '      <div data-dojo-type="hotplate.bd.Dashboard" data-dojo-props="title: \'Dashboard\'">Section One</div>' +
        '      <div data-dojo-type="dijit.layout.ContentPane" data-dojo-props="title: \'Two\'">Section Two</div>' +
        '      <div data-dojo-type="dijit.layout.ContentPane" data-dojo-props="title: \'Three\'">Section Three</div>' +
        '  </div>'+
        '</div>',

      postCreate: function(){


      }, // postCreate


   });

});

