


require( [ "hotplate/bd/BookingDojo", 'dojo/topic' ], function( BookingDojo, topic ){

  // Create the new BookingDojo application
  bookingDojo = new BookingDojo( { id: 'bookingDojo', region: 'center'  } );

  topic.subscribe( 'hotplate/hotHooks/startup/after/appContainer', function( parentWidget ){
    parentWidget.addChild( bookingDojo );
  });



});

