require([
   "dojo/_base/declare",
   "dojo/when",
   "dojo/store/Observable",
   "dojo/topic",
   "dojo/on",
   "dojo/dom",

   "dijit/_WidgetBase",
   "dijit/_TemplatedMixin",
   "dijit/_WidgetsInTemplateMixin",
   "dijit/Destroyable",
   "dijit/Dialog",
   "dijit/_OnDijitClickMixin",

   "dijit/layout/BorderContainer",
   "dijit/layout/StackContainer",
   "dijit/layout/TabContainer",
   "dijit/layout/ContentPane",

   "hotplate/hotDojoAuth/LogoutButton",
   "hotplate/hotDojoHooks/_TemplatedHooksMixin",
   "hotplate/hotDojoStores/stores",
   "hotplate/hotDojoWidgets/_OverlayMixin",
   "hotplate/hotMessages/messages",

   "hotplate/bd/AccessConfig",
   "hotplate/bd/AdminConfig",
   "hotplate/bd/UserConfig",

   ], function(
     declare
     , when
     , Observable
     , topic
     , on
     , dom

     , _WidgetBase
     , _TemplatedMixin
     , _WidgetsInTemplateMixin
     , Destroyable
     , Dialog
     , _OnDijitClickMixin

     , BorderContainer
     , StackContainer
     , TabContainer
     , ContentPane

     , LogoutButton
     , _TemplatedHooksMixin
     , stores
     , _OverlayMixin
     , messages

     , AccessConfig
     , AdminConfig
     , UserConfig

 ){

  // The user dialog with the config in it. Not displayed to start with
  var userDialog = new Dialog( {
     title: "Your account",
  });
  userDialog.addChild( new UserConfig({ userId: vars['hotDojoAuth']['userId'], recordStoreName: 'userConfigNoWorkspace' }) );

  var userConfigDiv = dom.byId('userConfig');
  userConfigDiv.innerHTML = "See your user configuration";

  // style="cursor:pointer"

  on( userConfigDiv, 'click', function(){
    userDialog.show();
    console.log( "Click" );
  });
   

});

