var util = require('util')
  , hotplate = require('hotplate')
  , path = require('path')
;

exports.hotHooks = hooks = {}


// Make the file "shared.js" visible and shared.
hooks.pageElements = function( done ){

  done( null, { moduleName: 'hotHandyCss', result: {
    csses: [ 'handyCss.css' ]
  } });
}

hooks.clientPaths = function( done ){
  done( null, { moduleName:'hotHandyCss', result: [ path.join(__dirname, '../client') ] } );
}


