var 
  hotplate =  require('hotplate')
, path = require('path')
;

exports.hotHooks = hooks = {}

hooks.run = function( done ){
  done( null );
}


hooks.pageElements = function( done ){

  done( null, { moduleName: 'hotDojo', result:{
    headLines: [ 
      '<link type="text/css" rel="stylesheet" href="/lib/dojo/dijit/themes/claro/claro.css" media="screen" />',
      '<script type="text/javascript" src="/lib/dojo/dojo/dojo.js" data-dojo-config="parseOnLoad: false, async:true,isDebug:false, cacheBust:false"></script>',
    ],

  } } );
}

// Simply activate path to client files
hooks.clientPaths = function( done ){
  done( null, { moduleName: 'hotDojo', result: [ path.join(__dirname, '../client') ] } );
}


