
require(['dojo/topic', 'dojo/ready', "hotplate/hotDojoAppContainer/AppContainer"  ], function( topic, ready, AppContainer ){

  // This global variable will be used by hotDojoAuth in case the password
  // needs to be retyped
  // FIXME: Setting a global variable with loginValiue in case the password needs to be retyped is just plain terrible. Fix this...
  loginValue = vars.hotDojoAppContainer.login;

  // Create the "application" object, and places them in the right spot.
  appContainer = new AppContainer( {} , 'appContainer');

  // When everything is ready(), other scripts are already subscribed to
  // topics (like the one generated by startup() ). That's a good time
  // to startup() the container widget!
  ready(function() {
    appContainer.startup();
  });

});

