define([
  'dojo/_base/declare',

  'dijit/form/ValidationTextBox',
  ], function(
    declare
  , ValidationTextBox
  ){
    return declare('hotplate.hotDojoAuth.ValidationEmail', [ ValidationTextBox ], {

      validator: function(value){

        var Validators = sharedFunctions.hotDojoAuth;

        var validation =  Validators.email(value);

        if( ! validation.result ){
          this.invalidMessage = validation.message;
          return false;
        }
        return true;

      },

      // invalidMessage: "Username not valid",
      // missingMessage: "Username is a mandatory field",

    });

  }
);
