// ...or like this (client paths)
hooks.clientPaths = function(){
  return [ path.join(__dirname, '../client') ];
}

hooks.stores = function(){
  return {
    loginAnon: { target: '/loginAnon', idProperty: '_id', sortParam: 'sortBy', },
    usersAnon: { target: '/usersAnon', idProperty: '_id', sortParam: 'sortBy', },
  }

}



