define([
  'dojo/_base/declare',
  'dojo/_base/lang',
  'dojo/topic',

  ], function(
    declare
  , lang
  , topic

  ){
    return  declare(null, {

      templatedHooks: true,

      constructor: function(){
        this.templatedHooks = true;
        topic.publish('hotplate/hotHooks/constructor/' + this.id, this);
      },

      buildRendering: function(){
        topic.publish('hotplate/hotHooks/buildRendering/before/' + this.id, this);
        this.inherited(arguments);
        topic.publish('hotplate/hotHooks/buildRendering/after/' + this.id, this);
      },

      destroyRendering: function(){
        topic.publish('hotplate/hotHooks/destroyRendering/before/' + this.id, this);
        this.inherited(arguments);
        topic.publish('hotplate/hotHooks/destroyRendering/after/' + this.id, this);
      },

      postCreate: function(){
        topic.publish('hotplate/hotHooks/postCreate/before/' + this.id, this);
        this.inherited(arguments);
        topic.publish('hotplate/hotHooks/postCreate/after/' + this.id, this);
      },

      startup: function(){
        topic.publish('hotplate/hotHooks/startup/before/' + this.id, this);
        this.inherited(arguments);
        topic.publish('hotplate/hotHooks/startup/after/' + this.id, this);
      },

      destroy: function(){
        topic.publish('hotplate/hotHooks/destroy/before/' + this.id, this);
        this.inherited(arguments);
        topic.publish('hotplate/hotHooks/destroy/after/' + this.id, this);
      }

    });
  }
);

