define([
  'hotplate/hotDojoStores/JsonRest',
  'dojo/domReady!',
  ],function(
  JsonRest
  ){


  // Stores
  var stores = {};

  // If the global variable for the stores is defined...
  if(
    typeof( vars ) == 'object' &&
    typeof( vars.hotStoresVars ) == 'object' &&
    typeof( vars.hotStoresVars.stores ) == 'object'
  ){

    var definedStores = vars.hotStoresVars.stores;
    for( var i in definedStores ){

      // Create the store
      stores[i] = new JsonRest({
        target: definedStores[i].target,
	  		idProperty: definedStores[i].idProperty,
        sortParam: definedStores[i].sortParam,
      });
    }
  }

  return stores;

});


