"use strict";

/*!
 * Module dependencies.
 */

var util = require('util')
  , hotplate = require('hotplate')
  , path = require('path')
;


hotplate.hotEvents.on( 'pageElements', 'hotCoreHandyCss', function( done ){

  done( null, {
    csses: [ 'handyCss.css' ]
  });

});


hotplate.hotEvents.on( 'clientPaths', 'hotCoreHandyCss', function( done ){
  done( null, [ path.join(__dirname, '../client') ] );
});


