"use strict";

var dummy
  , hotplate =  require('hotplate')
  , path = require('path')
;


// Simply activate path to client files
hotplate.hotEvents.on( 'clientPaths', 'hotDojoJade', function( done ){
  done( null, [ path.join(__dirname, '../client') ] );
})

hotplate.hotEvents.on( 'pageElements', 'hotDojoJade', function( done ){

  done( null, {
    jses:  ['jade.js' ],
  });

})

