
var 
  hotplate =  require('hotplate')
, path = require('path')
;

hotplate.hotEvents.on('pageElements', 'hotDojoWidgets', function( done ){
  done( null, { 
    csses: [ 'AlertBar.css', 'EditingWidget.css' ],
  });
});

// Simply activate path to client files
hotplate.hotEvents.on( 'clientPaths', 'hotDojoWidgets', function( done ){
  done( null, [ path.join(__dirname, '../client') ] );
})


